import {withOktaAuth} from "@okta/okta-react";
import React from "react";
import "../Stylesheets/GroupButton.css"

export default withOktaAuth(class GroupButton extends React.Component {

    state = {
        clicked: false
    }

    componentDidMount() {
        this.checkStyle()
    }

    onClickHandler = (e) =>{
        if (e.target.name !== "Everyone") {

            const group_id = this.props.group.id
            const user_id = this.props.user.id

            const payload = {
                "groups": this.props.group.id
            }

            const packet = {
                method: "PUT",
                headers: {
                    "credentials": "include",
                    "Accept": "Application/json",
                    "Content-type": "Application/json",
                },
                body: JSON.stringify(payload)
            }

            fetch(`"https://dev-49794790.okta.com/api/v1/groups/${group_id}/users/${user_id}"`, packet)
                .then(res => res.json())
                .then(console.log)
                .then(() => {
                    this.setState({
                        clicked: !this.state.clicked
                    })
                })
        }
    }

    checkStyle = () => {
        const user_groups = this.props.user.groups.map(group => group.id)
        if (user_groups.includes(this.props.group.id)) {
            this.setState({
                clicked: true
            })
        }
    }

    render() {
        return (
            <>
                <button
                    name={this.props.group.profile.name}
                    onClick={this.onClickHandler}
                    id={this.props.group.id}
                    className={this.state.clicked ? "clicked" : "not-clicked"}>
                    {this.props.group.profile.name}
                </button>
            </>
        )
    }
})
