import React from 'react'
import { withOktaAuth } from "@okta/okta-react";
import SignInWidget from "../Widgets/SignInWidget";

export default withOktaAuth(class Profile extends React.Component{

    state = {
        currentUser: null,
        groups: null,
        administrator: null,
    }

    componentDidMount = () => {
        const packet = {
            cache: "no-cache",
            credentials: "include",
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
            },
        }
        fetch("https://dev-49794790.okta.com/api/v1/users/me", packet)
            .then(res => res.json())
            .then(user => this.setState({
                currentUser: user
            }))
            .then(this.fetchGroups())
    }

    fetchGroups = () => {
        if (this.state.currentUser) {
            console.log(`https://dev-49794790.okta.com/api/v1/users/${this.state.currentUser.id}/groups`)
            const packet = {
                cache: "no-cache",
                credentials: "include",
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    "Accept": "application/json",
                },
            }

            fetch(`https://dev-49794790.okta.com/api/v1/users/${this.state.currentUser.id}/groups`, packet)
                .then(res => res.json())
                .then(groups => this.checkGroupForAdmin(groups))
        }

    }

    checkGroupForAdmin = (groups) => {
        let groupArray = groups.map(group => group.profile.name)
        console.log(groupArray)
        if (groupArray.includes("Administrators")) {
            this.setState({
                administrator: true,
            })
        } else {
            this.setState({
                administrator: false
            })
        }
    }




    renderUserInfo = () => {
        return (
            <div>
                <h3>Your Profile</h3>
                <div>Name: {this.props.authState.idToken.claims.name}</div>
                <div>E-Mail {this.props.authState.idToken.claims.email}</div>
                <SignInWidget />
            </div>
        )
    }



    render() {
        console.log(this.state.administrator)
        return(

            <div>
                {this.props.authState.idToken ? this.renderUserInfo() : null}
                {this.state.administrator ? <div>administrator</div> : null}
            </div>
        )
    }
})
