import {withOktaAuth} from "@okta/okta-react";
import React from "react";
import "../Stylesheets/GroupButton.css"

export default withOktaAuth(class GroupButton extends React.Component {

    state = {
        clicked: false
    }

    componentDidMount() {
        this.checkStyle()
    }

    checkStyle = () => {
        const user_groups = this.props.user.groups.map(group => group.id)
        if (user_groups.includes(this.props.group.id)) {
            this.setState({
                clicked: true
            })
        }
    }

    onClickHandler = (e) =>{

        this.setState({
            clicked: !this.state.clicked
        }, () => {
            const payload = {
                "groups": this.props.group.id
            }

            const packet = {
                method: "PUT",
                headers: {
                    "Accept": "Application/json",
                    "Content-type": "Application/json",
                },
                body: JSON.stringify(payload)
            }

            fetch("http://127.0.0.1:3000/okta_users/" + this.props.user.id, packet)
                .then(res => res.json())
                .then(() => this.checkStyle())
        }, () => this.props.updateGroups(this.props.group.id), "add")
    }

    render() {
        console.log(this.state.clicked)
        return (
            <>
                <button
                    name={this.props.group.profile.name}
                    onClick={this.onClickHandler}
                    id={this.props.group.id}
                    className={this.state.clicked ? "clicked" : "not-clicked"}>
                    {this.props.group.profile.name}
                </button>
            </>
        )
    }
})
