import React from 'react'
import './App.css';
import Navbar from "./Components/Layout/Navbar";
import { BrowserRouter as Router, Route } from "react-router-dom"
import { Security, SecureRoute, LoginCallback } from '@okta/okta-react';
import Login from "./Components/Auth/Login";

import Home from "./Components/Pages/Home"
import Staff from "./Components/Pages/Staff";

const oktaAuthConfig = {
    issuer: 'https://dev-49794790.okta.com/oauth2/default',
    clientId: '0oa5mxo7vYYXOeDWn5d6',
    redirectUri: window.location.origin + '/login/callback',
};

function onAuthRequired({ history }) {
    history.push("/login")
}

class App extends React.Component {


  render() {
    return (
        <Router>
            <Security issuer={oktaAuthConfig.issuer}
                      clientId={oktaAuthConfig.clientId}
                      redirect_uri={window.location.origin + "/implicit/callback"}
                      onAuthRequired={onAuthRequired}
            >
                <div className="App">
                    <Navbar />
                    <div className="container">
                        <Route path="/" exact={true} component={Home} />

                        <SecureRoute path={"/staff"} exact={true} component={Staff} />
                        <Route path="/login" render={() => <Login baseUrl="https://dev-49794790.okta.com" /> } />
                        <Route path="/login/callback" component={LoginCallback} />
                    </div>
                </div>
            </Security>
        </Router>
    );
  }

}

export default App;
