import React from 'react'
import { withOktaAuth } from "@okta/okta-react";
import SignInWidget from "../Widgets/SignInWidget";

export default withOktaAuth(class Profile extends React.Component{

    state = {
        currentUserID: null
    }

    componentDidMount = async() => {
        const user = await this.props.oktaAuth.getUser()
        const accessToken = this.props.authState.accessToken.accessToken

        const packet = {
            method: "GET",
            header: {
                Authorization: `Bearer ${accessToken}`,
                "Content-Type": "application/json",
                "Accepts": "application/json",
            },
        }

        fetch("https://dev-49794790.okta.com/api/v1/users/", packet)
            .then(res => res.json())
            .then(console.log)

    }

    renderUserInfo = () => {

        return (
            <div>
                <h3>Your Profile</h3>
                <div>Name: {this.props.authState.idToken.claims.name}</div>
                <div>E-Mail {this.props.authState.idToken.claims.email}</div>
                <SignInWidget />
            </div>
        )
    }

    render() {
        return(

            <div>
                {this.props.authState.idToken ? this.renderUserInfo() : null}
            </div>
        )
    }
})
