import {withOktaAuth} from "@okta/okta-react";
import React from "react";
import "../Stylesheets/GroupButton.css"

export default withOktaAuth(class GroupButton extends React.Component {

    state = {
        clicked: ''
    }

    componentDidMount() {
        this.checkStyle()
    }

    onClickHandler = () =>{
        this.setState({
            clicked: !this.state.renderEditForm
        })
    }

    checkStyle = () => {
        const user_groups = this.props.user.groups.map(group => group.id)
        if (user_groups.includes(this.props.group.id)) {
            this.setState({
                clicked: true
            })
        }
        this.setState({
            clicked: false
        })
    }

    render() {
        return (
            <>
                <button onClick={this.onClickHandler} className={this.state.clicked ? "clicked" : "not-clicked"}>
                    {this.props.group.profile.name}
                </button>
            </>
        )
    }
})
