import React from "react";
import "../Stylesheets/GroupButton.css"

export default withOktaAuth(class GroupButton extends React.Component {

    state = {
        clicked: false
    }

    onClickHandler = () =>{
        this.setState({
            clicked: !this.state.renderEditForm
        })
    }

    checkStyle = () => {
        const list = this.props.user.groups.map(group => group.id === this.props.group.id)

        return "clicked"
    }

    render() {
        return (
            <>
                <button onClick={this.onClickHandler} className={this.checkStyle()}>
                    {this.props.group.profile.name}
                </button>
            </>
        )
    }
})
