import React from 'react'
import { withOktaAuth } from "@okta/okta-react";


export default withOktaAuth(class EditUserForm extends React.Component {

    state = {
        firstName: '',
        lastName: '',
        email: '',
        login: '',
        mobilePhone: '',
        groups: [],
    }

    componentDidMount() {
        this.setState({
            firstName: this.props.user.firstName,
            lastName: this.props.user.lastName,
            email: this.props.user.email,
            login: this.props.user.login,
            mobilePhone: this.props.user.mobilePhone,
        })
    }

    onChangeHandler = (e) => {
        if (e.target.name === "firstName") {
            this.setState({
                firstName: e.target.value
            })
        } else if (e.target.name === "lastName") {
            this.setState({
                lastName: e.target.value
            })
        } else if (e.target.name === "email") {
            this.setState({
                email: e.target.value
            })
        } else if (e.target.name === "login") {
            this.setState({
                login: e.target.value
            })
        } else if (e.target.name === "mobilePhone") {
            this.setState({
                mobilePhone: e.target.value
            })
        }
    }

    onClickHandler = (e) => {

    }

    updateUserGroups = () => {
        let user = this.props.user
        console.log(user)
        this.state.groups.forEach(group => {

            const packet = {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Accept": "application/json",
                    "Authorization": "SSWS 00WhTQiM4FjUkWkH3oIza6jFaJjS9gGHf8HRgK2ReG"
                }
            }
            console.log(`"https://dev-49794790.okta.com/api/v1/groups/${group}/users/${user.id}/"`)

            fetch(`"https://dev-49794790.okta.com/api/v1/groups/${group}/users/${user.id}/"`, packet)
                .then(res => res.json())
                .then(console.log)
        })
    }



    onSubmitHandler = (e) => {
        e.preventDefault()
        console.log(this.state)

    }

    render() {
        return(
            <div>
                <h6>Edit User</h6>
                <form onSubmit={this.onSubmitHandler}>
                    <input
                        type="text"
                        name="firstName"
                        value={this.state.firstName}
                        onChange={this.onChangeHandler}
                        placeholder="First Name" />
                    <input
                        type="text"
                        name="lastName"
                        value={this.state.lastName}
                        onChange={this.onChangeHandler}
                        placeholder="Last Name" />
                    <input
                        type="text"
                        name="email"
                        value={this.state.email}
                        onChange={this.onChangeHandler}
                        placeholder="Email" />
                    <input
                        type="text"
                        name="login"
                        value={this.state.login}
                        onChange={this.onChangeHandler}
                        placeholder="Login" />
                    {/*<input*/}
                    {/*    type="text"*/}
                    {/*    name="mobilePhone"*/}
                    {/*    value={this.state.mobilePhone}*/}
                    {/*    onChange={this.onChangeHandler}*/}
                    {/*    placeholder="Phone" /><br />*/}
                    <input type="submit" />

                </form>
                <div>
                    <h6>Add User to Groups</h6>
                    <div>
                        <button value="00g5iptquShLgpDGn5d6" name="administrators" onClick={this.onClickHandler}>Make Administrator</button>
                    </div>
                </div>
            </div>


        )
    }
})
