import {withOktaAuth} from "@okta/okta-react";
import React from "react";
import "../Stylesheets/User.css"
import EditUserForm from "../Forms/EditUserForm";

export default withOktaAuth(class User extends React.Component {

    state = {
        renderEditForm: false
    }

    onClickHandler = () =>{
        this.setState({
            renderEditForm: !this.state.renderEditForm
        })
    }

    render() {
        return (
            <div>
                <div className="user-container" onClick={this.onClickHandler}>
                    <span>{this.props.user.profile.firstName}</span>
                    <span>{this.props.user.lastName}</span>
                    <span>{this.props.user.mobilePhone}</span>
                    <span>{this.props.user.email}</span>

                    <span> <a
                        href={`"https://dev-49794790.okta.com/api/v1/users/${this.props.user.oktaid}/`}
                        target="_blank">{this.props.user.oktaid}</a>
                    </span>

                    <span><button>EDIT</button></span>
                </div>

                <div>
                    {this.state.renderEditForm ? <EditUserForm user={this.props.user} backendUrl={this.props.backendUrl}/> : null}
                </div>

            </div>

        )
    }

})
