import React from 'react'
import {withOktaAuth} from "@okta/okta-react";
import User from "../Cards/User";
import CreateUserForm from "../Forms/CreateUserForm";


export default withOktaAuth(class Admin extends React.Component{

    state = {
        users: null,
        groups: null,
        create_user_form: false,
    }



    componentDidMount() {
        this.fetchUsers()
        this.fetchGroups()
    }

    toggleCreateUserForm = () => {
        this.setState({
            create_user_form: !this.state.create_user_form
        })
    }





    fetchGroups = async () => {

        const packet = {
            method: "GET",
            headers: {
                'content-type': 'application/json',
                accept: 'application/json',
                authorization: `Bearer ${await this.props.oktaAuth.getAccessToken()}`,
            },
        }
        fetch("http://127.0.0.1:3000/okta_groups/", packet)
            .then(res => res.json())
            .then(groups => this.setState({groups: groups}))
    }

    fetchUsers = () => {
        const packet = {
            method: "GET",
            headers: {
                'Content-Type': "application/json",
                'Accept': "application/json",
            },
        }
        fetch("http://127.0.0.1:3000/okta_users/", packet)
            .then(res => res.json())
            .then(users => this.setState({users: users}))
    }

    renderUsers = () => {
        return this.state.users.map(
            user => <User
            key={user.id}
            user={user}
            groups={this.state.groups}
            backendUrl={this.props.backendUrl} />)
    }

    render() {
        return(
            <div>
                <h3 className="page-header">User Administration</h3>

                <h2 className="content-header">Current Users</h2>
                <div className="container">
                        {this.state.users ? this.renderUsers() : null}
                 </div>

                <div>
                    <button onClick={this.toggleCreateUserForm}>Create User</button>
                    {this.state.create_user_form ? <CreateUserForm backendUrl={this.props.backendUrl}/> : null}
                </div>
            </div>


        )
    }
})
